import React, { Component } from "react";
import QuoteContainer from "./quote-container";

class Page extends Component {
  constructor(props) {
    super(props);
    this.state = {
      styles: {
        background: "",
        width: "100vw",
        height: "100vh"
      }
    };
    this.handleData = this.handleData.bind(this);
  }

  handleData(rgb) {
    this.setState({
      background: `#${rgb}`
    });
  }
  render() {
    return (
      <div style={this.state.styles}>
        <h1 style={{ color: "black" }}>{this.state.background}</h1>
        <QuoteContainer color={this.handleData} />
      </div>
    );
  }
}

export default Page;
