import React, { Component } from "react";
import QuoteContainer from "./quote-container";

class Page extends Component {
  constructor(props) {
    super(props);
    this.state = {
      styles: {
        background: `rgb(${[255, 100, 100]})`,
        width: "100vw",
        height: "100vh"
      },
      allSongs: [
        "https://soundseric.s3.us-east-2.amazonaws.com/Catsa.mp3",
        "https://soundseric.s3.us-east-2.amazonaws.com/Bootsa.mp3"
      ]
    };
    this.handleData = this.handleData.bind(this);
    this.createButtonLabel = this.createButtonLabel.bind(this);
  }

  createButtonLabel(item) {
    let regex = /\w+(?=.mp3)/;
    let matched = item.match(regex);
    return matched;
  }

  handleData() {
    let rgb = [];
    for (let i = 0; i < 3; i++) {
      rgb.push(Math.floor(Math.random() * 256));
    }
    console.log(rgb);
    this.setState({
      styles: {
        background: `rgb(${rgb})`,
        width: "100vw",
        height: "100vh"
      }
    });
    console.log(this.state.styles.background);
  }
  render() {
    return (
      <div style={this.state.styles}>
        <h1 style={{ color: "black" }}>{this.state.styles.background}</h1>
        {this.state.allSongs.map(item => (
          <QuoteContainer
            song={item}
            name={this.createButtonLabel(item)}
            color={this.handleData}
          />
        ))}
      </div>
    );
  }
}

export default Page;
