import React, { Component } from "react";
import SoundContainer from "./sound-tile";

class Page extends Component {
  constructor(props) {
    super(props);
    this.state = {
      styles: {
        background: `rgb(${[255, 100, 100]})`,
        width: "100vw",
        height: "100vh"
      }
    };
    this.handleData = this.handleData.bind(this);
    this.createButtonLabel = this.createButtonLabel.bind(this);
  }

  createButtonLabel(item) {
    let regex = /\w+(?=.mp3)/;
    let matched = item.match(regex);
    return matched;
  }

  handleData() {
    let rgb = [];
    for (let i = 0; i < 3; i++) {
      rgb.push(Math.floor(Math.random() * 256));
    }
    console.log(rgb);
    this.setState({
      styles: {
        background: `rgb(${rgb})`,
        width: "100vw",
        height: "100vh"
      }
    });
    console.log(this.state.styles.background);
  }
  render() {
    return (
      <div style={this.state.styles}>
        {/* <h1 style={{ color: "black" }}>{this.state.styles.background}</h1>*/}
        <SoundContainer color={this.handleData} />
      </div>
    );
  }
}

export default Page;
