import React, { Component } from "react";
import QuoteContainer from "./quote-container";

class Page extends Component {
  constructor(props) {
    super(props);
    let rgb = [0, 0, 0];
    this.state = {
      styles: {
        backgroundColor: `rgb(${rgb})`,
        width: "100vw",
        height: "100vh"
      }
    };
  }

  handleData() {
    rgb = rgb.map(item => item + Math.floor(Math.random() * 256));
    this.setState({
      backgroundColor: `rgb(${rgb})`
    });
  }
  render() {
    return (
      <div style={this.state.styles}>
        <QuoteContainer color={this.state.styles.background} />
      </div>
    );
  }
}

export default Page;
