import React, { Component } from "react";

class QuoteContainer extends Component {
  constructor(props) {
    super(props);
    this.state = {
      noiseId: "",
      song: "BeatSound.mp3"
    };
    this.handleClick = this.handleClick.bind(this);
  }

  handleClick() {
    this.props.color();
    let sound = new Audio(this.state.song);
    sound.play();
  }

  render() {
    return (
      <div>
        <audio controls>
          <source src={this.state.song} type="audio/mp3" />
        </audio>
        <button onClick={this.handleClick} className="btn btn-lg" />
      </div>
    );
  }
}

export default QuoteContainer;
