import React, { Component } from "react";
import QuoteContainer from "./quote-container";

class Page extends Component {
  constructor(props) {
    super(props);
    this.state = {
      styles: {
        background: "white",
        width: "100vw",
        height: "100vh"
      }
    };
    this.handleData = this.handleData.bind(this);
  }

  handleData() {
    let rgb = [0, 0, 0];
    rgb = rgb.map(item => item + Math.floor(Math.random() * 256));
    this.setState({
      background: `rgb(${rgb})`
    });
    console.log(this.state.styles.background);
  }
  render() {
    return (
      <div style={this.state.styles}>
        <h1 style={{ color: "black" }}>{this.state.styles.background}</h1>
        <QuoteContainer color={this.handleData} />
      </div>
    );
  }
}

export default Page;
